import { getNextKeyDef } from "@testing-library/user-event/dist/keyboard/getNextKeyDef";
import React, { Component } from "react";
import NewsItem from "./NewsItem";
// import PropTypes from 'prop-types'

export default class News extends Component {
  // static propTypes = {
  //   prop: PropTypes
  // }

  constructor() {
    super();
    console.log("News Constructor");
    this.state = {
      article: [],
      loading:false,
      pageNo:1,
      url: `https://newsapi.org/v2/top-headlines?category=business&apiKey=1fdd86d558504b8983e5906276fb8934&pageSize=20&page=${this.state.page}`
    };
  }

async componentDidMount(){
  let url = this.state.url;
  let promise = await fetch(url);
  let parsedData = await promise.json();
  this.setState({
    article: parsedData.articles
  })
}

NextKey = async ()=>{
  this.setState({
    pageNo: this.state.pageNo+1
  })
}

PrevKey= ()=>{

}
  render() {
    return (
      <>
        <div className="container my-3">
          <h2>Top Headlines.....</h2>
          <div className="row">
            {this.state.article.map((element)=>{
                return <div className="col-md-4" key={element.url}>
                          <NewsItem
                            title={(element.title!=null)?element.title.slice(0, 45):element.title}
                            description={(element.description!=null)?element.description.slice(0, 88):element.description}
                            ImageUrl={element.urlToImage}
                            url = {element.url}
                          />
                        </div>
            })}

          </div>
          <div className="container d-flex justify-content-between">
            <button type="button" disabled={this.pageNo<=1} className="btn btn-dark" onClick={this.PrevKey}>&larr; Previous</button>
            <button type="button" className="btn btn-dark" onClick={this.NextKey}>Next &rarr; </button>
          </div>
        </div>
      </>
    );
  }
}
