import React, { Component } from "react";
import NewsItem from "./NewsItem";
// import PropTypes from 'prop-types'

export default class News extends Component {
  // static propTypes = {
  //   prop: PropTypes
  // }

  constructor() {
    super();
    console.log("News Constructor");
    this.state = {
      article: [],
      loading:false
    };
  }

async componentDidMount(){
  let url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=1fdd86d558504b8983e5906276fb8934";
  let promise = await fetch(url);
  let parsedData = await promise.json();
  this.setState({
    article: parsedData.articles
  })
}

  render() {
    return (
      <>
        <div className="container my-3">
          <h2>Top Headlines.....</h2>
          <div className="row">
            {this.state.article.map((element)=>{
                return <div className="col-md-4" key={element.url}>
                          <NewsItem
                            title={(element.title!=null)?element.title.slice(0, 45):element.title}
                            description={(element.description!=null)?element.description.slice(0, 88):element.description}
                            ImageUrl={element.urlToImage}
                            url = {element.url}
                          />
                        </div>
            })}

          </div>
        </div>
        <div className="container">
        <button type="button" className="btn btn-dark">Previous</button>
        <button type="button" className="btn btn-dark">Next</button>
        </div>
      </>
    );
  }
}
